import React, { useState } from "react"

import bcrypt from 'bcryptjs'

async function getSalt (username) {
  let salt = ""
  //get salt value
  await fetch(get_salt_link + `?username=${username}`, {
    method: "GET",
  }).then((response) => response.text()).
    then((result) => salt = result)
}

const SUCCESSFUL_LOGIN_MSG = "Login successful"
function LoginPage () {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const get_salt_link = `http://localhost:63010/auth/getsalt`

  const handleUsernameChange = (event) => {
    setUsername(event.target.value)
  }

  const handlePasswordChange = (event) => {
    setPassword(event.target.value)
  }

  const handleSubmit = async (event) => {
    event.preventDefault()
    if (username.length === 0 || password.length === 0) {
      alert("Empty Username or password")
      return
    }
    // TODO: Add login logic here
    // get salt

    const hash = bcrypt.hashSync(password, salt)

    let login_response = ""
    const request_link = `http://localhost:63010/auth/oauth/token?client_id=XcWebApp&grant_type=password&username=${username}&password=${hash}`
    try {
      const response = await fetch(request_link, {
        method: "POST"
      }).then((response) => {
        return response.json()
      }).then((data) => {
        login_response = data
        // 将 JWT 令牌存储在 localStorage 中
        console.log(login_response.access_token)
        localStorage.setItem('jwtToken', login_response.access_token)
      })
    } catch {
      alert("login unsuccessful")
    }


  }

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={handleUsernameChange}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default LoginPage
