import { useState, useEffect } from "react"
import { useLocation } from 'react-router-dom'
import '../axios_interceptor'
import { aes_encrypt } from "../encrypt"
import axios from "axios"
import { storage_url } from "../constants"

function Confirm_Open_Account () {
  const [username, setUsername] = useState('')
  const [confirmcode, setConfirmcode] = useState('')
  const [pinNum, setPinNum] = useState('')
  const { search } = useLocation()
  const queryParams = new URLSearchParams(search)
  useEffect(() => {

    setUsername(queryParams.get('username'))
    setConfirmcode(queryParams.get('confirmcode'))


  })

  async function submit_confirm_account () {
    const data = {
      username: username,
      confirmCode: confirmcode,
      pinNum: pinNum
    }
    const encrypted_msg = aes_encrypt(data)
    try {
      const response = await axios.post(storage_url + `/account/confirm?confirm_msg=${encrypted_msg}`)
      if (response.data.code == '-1') {
        alert("Something goes wrong Please log in again")
      } else {
        alert(response.data.msg)
      }
    } catch (error) {
      alert("Something goes wrong Please log in again")
    }
  }

  return (
    <div>
      <label>
        PIN:
        <input type="password" value={pinNum} onChange={e => setPinNum(e.target.value)} />
      </label>
      <button type="submit">Submit</button>
    </div>
  )
}
export default Confirm_Open_Account
