import { useState } from 'react'
import Header from '../Utils/header'

function BankAccountForm () {
  const [creditType, setCreditType] = useState('debit') // 默认选项是借记卡
  const [isChecked, setIsChecked] = useState(false)

  const handleCreditTypeChange = (event) => {
    setCreditType(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    // 在这里处理表单提交
  }

  function handleCheckboxChange () {
    isChecked = !isChecked
  }

  return (
    <form onSubmit={handleSubmit}>
      <Header></Header>

      <div>
        <label htmlFor="creditType">Credit Type:</label>
        <select id="creditType" name="creditType" value={creditType} onChange={handleCreditTypeChange}>
          <option value="debit">Debit Card</option>
          <option value="credit">Credit Card</option>
        </select>
      </div>
      <p>
        <label>
          <input type="checkbox" checked={isChecked} onChange={handleCheckboxChange} />
          我已经阅读并同意了开户协议
        </label>
      </p>
      <button disabled={!isChecked}>
        确认同意开户
      </button>
    </form>
  )
}
export default BankAccountForm
