import React, { useState } from "react"
import crypto from 'crypto'

function LoginPage () {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const get_salt_link = `http://localhost:63010/auth/getsalt`

  const handleUsernameChange = (event) => {
    setUsername(event.target.value)
  }

  const handlePasswordChange = (event) => {
    setPassword(event.target.value)
  }

  function sha256 (password) {
    const hash = crypto.createHash('sha256')
    hash.update(password)
    return hash.digest('hex')
  }

  const handleSubmit = async (event) => {
    event.preventDefault()
    if (username.length == 0 || password.length == 0) {
      alert("Empty Username or password")
      return
    }
    console.log(`Username: ${username}, Password: ${password}`)
    // TODO: Add login logic here
    // get salt
    let salt = ""
    const salt_request = await fetch(get_salt_link + `?username=${username}`, {
      method: "GET",
    }).then((response) => response.text()).
      then((result) => salt = result)
    console.log(salt)
    const hash = bcrypt.hashSync(password, salt)
    console.log(hash)
    const request_link = `http://localhost:63010/auth/login?username=${username}&password=${hash}`
    const response = await fetch(request_link, {
      method: "POST"
    })
  }

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={handleUsernameChange}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default LoginPage
