import React, { useState, useEffect } from 'react'
import axios from 'axios'
import Header from '../Utils/header'
import './style.scss'
import '../axios_interceptor'
import { storage_url } from '../constants'

function BankCardsInfo () {
  const [cards, setCards] = useState([])

  const card_info_request = async () => {
    try {
      const response = await axios.get(storage_url + "/storage/getCardInfo")
      if (response.data.code === '-1') {
        //alert("Something goes wrong Please log in again")
      } else {
        console.log(response.data.msg)
      }
    } catch (error) {
      //alert("Something goes wrong Please log in again")
    }
  }
  useEffect(() => {
    // 在这里发起请求，获取所有银行卡的信息
    // 以下是一个简单的示例
    card_info_request()
  })

  return (
    <div className='align_center'>
      <Header></Header>
      <h1 className='align_center'>All Bank Cards</h1>
      <table>
        <thead>
          <tr>
            <th>Card Number</th>
            <th>Card Type</th>
            <th>Expiration Date</th>
          </tr>
        </thead>
        <tbody>
          {cards.map((card) => (
            <tr key={card.id}>
              <td>{card.cardNumber}</td>
              <td>{card.cardType}</td>
              <td>{card.expirationDate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

export default BankCardsInfo
